(5, 'contact.html', '<h2>Kontakt</h2>\r\n<p>Masz pytania dotyczące największych mostów świata? Chcesz dowiedzieć się więcej o naszej stronie lub podzielić\r\n  się ciekawostką? Wypełnij poniższy formularz, a odpowiemy na Twoje pytania jak najszybciej!</p>\r\n<form action=\"mailto:example@example.com\" method=\"post\">\r\n  <label for=\"name\">Twoje imię:</label><br>\r\n  <input type=\"text\" id=\"name\" name=\"name\" required><br>\r\n  <label for=\"email\">Twój email:</label><br>\r\n  <input type=\"email\" id=\"email\" name=\"email\" required><br>\r\n  <label for=\"message\">Wiadomość:</label><br>\r\n  <textarea id=\"message\" name=\"message\" required></textarea><br>\r\n  <button type=\"submit\">Wyślij</button>\r\n  <button type=\"reset\">Wyczyść</button>\r\n</form>\r\n<aside>\r\n  <h3>Lista kontaktów</h3>\r\n  <ul>\r\n    <li>Redakcja: redakcja@mosty.pl</li>\r\n    <li>Inżynier: inzynier@mosty.pl</li>\r\n    <li>Wsparcie techniczne: wsparcie@mosty.pl</li>\r\n  </ul>\r\n</aside>', 1),
(6, 'scripts.html', '<h2>Skrypty JavaScript</h2>\r\n<div id=\'skrypt_jeden\'>\r\n    <p>Kliknij przycisk, aby zmienić kolor tła strony.</p>\r\n    <form method=\'post\' name=\'background\'>\r\n        <button type=\'button\' onclick=\'changeBackground(\"#FFF000\")\'>Żółty</button>\r\n        <button type=\'button\' onclick=\'changeBackground(\"#000000\")\'>Czarny</button>\r\n        <button type=\'button\' onclick=\'changeBackground(\"#FFFFFF\")\'>Biały</button>\r\n        <button type=\'button\' onclick=\'changeBackground(\"#00FF00\")\'>Zielony</button>\r\n        <button type=\'button\' onclick=\'changeBackground(\"#0000FF\")\'>Niebieski</button>\r\n        <button type=\'button\' onclick=\'changeBackground(\"#FF8000\")\'>Pomarańczowy</button>\r\n        <button type=\'button\' onclick=\'changeBackground(\"#C0C0C0\")\'>Szary</button>\r\n        <button type=\'button\' onclick=\'changeBackground(\"#FF0000\")\'>Czerwony</button>\r\n        <button type=\'button\' onclick=\'changeBackground(getRandomColor())\'>Losowy kolor</button>\r\n    </form>\r\n</div>\r\n<div id=\"skrypt_dwa\">\r\n    <div id=\'zegarek\'></div>\r\n    <div id=\'data\'></div>\r\n    <div id=\'dzienTygodnia\'></div>\r\n</div>', 1),
